mapper.update:
UPDATE COMPUTER_KEY
SET IS_DELETED='Y'
WHERE KEY=#{key}

UPDATE COMPUTER_KEY
SET COUNT = COUNT + #{offset}
WHERE KEY=#{key}

mapper.select:
SELECT KEY FROM COMPUTER_KEY
WHERE IS_DELETED = 'N'
AND GMT_CREAT BETWEEN date_sub(now(),interval 6 month) and now();
ORDER BY COUNT DESC
LIMIT 0,#{total}

SELECT COUNT(*) FROM COMPUTER_KEY
WHERE KEY = #{key}
AND IS_DELETED = 'N'
